import React, {useContext} from 'react';
import {Link, NavLink} from "react-router-dom";

import {CurrentUserContext} from 'contexts/currentUser';

const TopBar = () => {
  const [currentUserState] = useContext(CurrentUserContext);

  return (
    <nav className="navbar navbar-light">
      <div className="container">
        <Link to="/" className="navbar-brand">Medium</Link>
        <ul className="nav navbar-nav pull-xs-right">
          <li className="nav-item">
            <NavLink to="/" exact className="nav-link">Home</NavLink>
          </li>
          {currentUserState.isLoggedIn ?
            <>
              <li className="nav-item">
                <NavLink to="/articles/new" exact className="nav-link">
                  <i className="ion-compose" />
                  &nbsp; New post
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink to="/settings" exact className="nav-link">
                  <i className="ion-gear-a" />
                  &nbsp; Settings
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink to={`/profile/${currentUserState.currentUser.userName}`} exact className="nav-link">
                  <img className="user-pic"
                       src={currentUserState.currentUser.image}
                       alt={currentUserState.currentUser.userName}/>
                  &nbsp; {currentUserState.currentUser.userName}
                </NavLink>
              </li>
            </>
            :
            <>
              <li className="nav-item">
                <NavLink to="/login" exact className="nav-link">Sign in</NavLink>
              </li>
              <li className="nav-item">
                <NavLink to="/register" exact className="nav-link">Sign up</NavLink>
              </li>
            </>}
        </ul>
      </div>
    </nav>
  );
};

export default TopBar;