import React from 'react';
import {Switch, Route} from 'react-router-dom';
//
import GlobalFeed from 'pages/globalFeed';
import TagFeed from 'pages/tagFeed';
import YourFeed from 'pages/yourFeed';
import Article from 'pages/article';
import Authentication from 'pages/authentication';
import CreateArticle from 'pages/createArticle';
import EditArticle from 'pages/editArticle';
import Settings from 'pages/settings';
import UserProfile from 'pages/userProfile';

const Routes = () => {
  return (
    <Switch>
      <Route path="/" exact component={GlobalFeed} />
      <Route path="/profile/:slug" exact component={UserProfile} />
      <Route path="/profile/:slug/favorites" exact component={UserProfile} />
      <Route path="/settings" exact component={Settings} />
      <Route path="/articles/new" exact component={CreateArticle} />
      <Route path="/articles/:slug/edit" exact component={EditArticle} />
      <Route path="/feed" exact component={YourFeed} />
      <Route path="/tags/:slug" exact component={TagFeed} />
      <Route path="/login" exact component={Authentication} />
      <Route path="/register" exact component={Authentication} />
      <Route path="/articles/:slug" exact component={Article} />
    </Switch>
  );
};

export default Routes;